import React from 'react';
import Image from 'react-bootstrap/Image'
import Button from 'react-bootstrap/Button'
import Jumbotron from 'react-bootstrap/Jumbotron'
import Container from 'react-bootstrap/Container'
class DisplayAgg extends React.Component{
    constructor(props) {
        super(props);
    }
    render(){
        var mysum = 0;
        var i = 0;
        for(i = 0; i < 5; i++){
            if(this.props.list[i]!=='None'){
                mysum = mysum+this.props.list[i].advantage
            }
        }
        return (
            <>
            <Container style={{color: 'black', backgroundColor:'lightskyblue'}}>
                <h1>Total Advantage</h1>
                <p>
                {mysum.toFixed(2)}
                </p>
            </Container>

            {this.props.list.map(item => 
                <div style={{ width:'100%', height: 'calc(17vh - 16px)', display:'flex',flexDirection:'row',alignItems:'center',justifyItems:'center',justifyContent:'center',alignContent:'Center' }}>
                    <Image src={item.image} thumbnail style={{ width:'40%', height: '80%'}}/>
                    <Button variant="primary" onClick={(e) => this.props.removeFromAgg(item, e)} style={{ width:'80px', height: '64px'}}> Remove </Button>
                </div>
            )}
            </>
        );
    }
}
export default DisplayAgg;
