import React from 'react';
import Button from 'react-bootstrap/Button'
import Card from 'react-bootstrap/Card'
class DisplayAgg extends React.Component{
    constructor(props) {
        super(props);
    }
    render(){
        var mysum = 0;
        var i = 0;
        for(i = 0; i < 5; i++){
            if(this.props.list[i]!='None'){
                mysum = mysum+this.props.list[i].advantage
            }
        }
        return (
            <>
            Total Advantage: {mysum.toFixed(2)}<br/>
            {this.props.list.map(item => 
                <>
                <Card style={{ width: '16.66%', color:'white'}} bg='dark'>
                <Card.Img variant="top" src={item.image}/>
                <Card.Body>
                <Card.Title>{item.name}</Card.Title>
                <Card.Subtitle>
                    Attribute: {item.attribute} <br/>
                    Role: {item.role} <br/>
                    Advantage: {item.advantage}%
                </Card.Subtitle>
                <Button variant="primary" onClick={(e) => this.props.handler(item, e)}> Add to Draft</Button>
                </Card.Body>
                </Card>
                <div>

                {item.img}
                </div>
                </>
            )}
            </>
        );
    }
}
export default DisplayAgg;
