import React from 'react';
import Image from 'react-bootstrap/Image'
import Button from 'react-bootstrap/Button'
class DisplayAgg extends React.Component{
    constructor(props) {
        super(props);
    }
    render(){
        var mysum = 0;
        var i = 0;
        for(i = 0; i < 5; i++){
            if(this.props.list[i]!=='None'){
                mysum = mysum+this.props.list[i].advantage
            }
        }
        return (
            <>
            Total Advantage: {mysum.toFixed(2)}<br/>
            {this.props.list.map(item => 
                <div style={{ width:'100%', height: 'calc(17vh - 16px)', display:'flex',flexDirection:'row',alignContent:'center',justifyContent:'center' }}>
                    <Image src={item.image} thumbnail style={{ width:'40%', height: '80%'}}/>
                    <Button variant="primary" onClick={(e) => this.props.handler(item, e)} style={{ width:'20px', height: '16px'}}> Remove </Button>
                </div>
            )}
            </>
        );
    }
}
export default DisplayAgg;
