import React from 'react';
import Button from 'react-bootstrap/Button'
import Card from 'react-bootstrap/Card'
class DisplayAgg extends React.Component{
    constructor(props) {
        super(props);
    }
    render(){
        var mysum = 0;
        var i = 0;
        for(i = 0; i < 5; i++){
            if(this.props.list[i]!='None'){
                mysum = mysum+this.props.list[i].advantage
            }
        }
        return (
            <>
            Total Advantage: {mysum.toFixed(2)}<br/>
            {this.props.list.map(item => 
                <>
                <Card style={{ width: '20%', color:'white'}} bg='dark'>
                <Card.Img variant="top" src={item.image}/>
                <Card.Body>
                <Card.Title>{item.name}</Card.Title>
                <Button variant="primary" onClick={(e) => this.props.handler(item, e)}> Remove </Button>
                </Card.Body>
                </Card>
                </>
            )}
            </>
        );
    }
}
export default DisplayAgg;
